db.collection("Events").orderBy("date").onSnapshot(n=>{listEvent(n.docs.reverse())});const listEvent=n=>{let a="";n.forEach(n=>{const e=n.data();let t="";storage.ref("events/"+e.title).getDownloadURL().then(s=>{t=s,a+=`<div class="card horizontal event-card" id='${n.id}'>\n        <div class="card-image">\n          <img class="mobile-display-none" src=${t} class="fadeIn">\n        </div>\n        <div class="card-stacked">\n          <div class="card-content">\n            <span class="card-title">${e.title}</span>\n            <p>${e.description}\n            </p>\n            <span class='date'>Date : <span style='color: #2487cd !important;'>${e.date.toDate().toDateString()}</span></span>\n            <span class='venue'>Venue : <span style='color: #2487cd !important;'>${e.venue}</span></span>\n          </div>\n          <div class="card-action">\n            <a href=${e.link} target="_blank">${e.linkName}</a>\n          </div>\n        </div>\n      </div>\n        `,document.querySelector("#eventList").innerHTML=a})})};